mapper.select:
SELECT l.*, r.region_major, r.region_minor FROM tb_library AS l inner join tb_region AS r on l.region_code = r.region_code
WHERE ${sk} like CONCAT('%',#{sv},'%')
LIMIT #{currentPage}, #{rowPerPage}

mapper.update:
UPDATE tb_library
region_code=#{region.regionCode}, library_name=#{libraryName}, library_setup=#{librarySetup}, library_open=#{libraryOpen}, library_tel=#{libraryTel}, library_page=#{libraryPage}, library_tel= #{libraryTel}, library_reg_date=NOW(), officer_id=#{officerId}
library_code=#{libraryCode}
